        rightAnswerId: 4,
        id: 1,
        answers: [
          {text: 'Нихуя, а ты шо?', id:1},
          {text: 'Нормас, на работе', id:2},
          {text: 'Та пиздец, дядь...', id:3},
          {text: 'Го вечером в ТСР', id:4},
        ]
      },
      {
        question: 'Что никогда не любил Женя?',
        rightAnswerId: 4,
        id: 2,
        answers: [
          {text: 'Погоду в Украине', id:1},
          {text: 'Корону', id:2},
          {text: 'Малолеток', id:3},
          {text: 'Осознанность', id:4},
        ]
      }
    ]
  }

